package task1;

import java.util.Scanner;

public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner (System.in);
		int n;
		System.out.print("Input n");
		n=sc.nextInt();
		for(int i=0; i<n;i++){
			if(i%2==0){
				System.out.print("+++");
				}else{
					System.out.print("***");
			for(int j=0; j<n;j++){
				if(j%2==0){
					System.out.println("+++");
					}else{
						System.out.println("***");
				}
		}
	}
		}
	}
}

	











	
	



